function showDiv(idx) {
  var x = document.getElementsByClassName("files");
  if(x[idx].style.display == "none" || x[idx].style.display == "")
    x[idx].style.display = "block";
  else x[idx].style.display = "none";
}

$('#m_proj_title').on('click', function(){
  var title = document.querySelector("#m_proj_title");
  if(title.innerHTML === "Kai kurie mokykliniai projektai ↑")
    title.innerHTML = "Kai kurie mokykliniai projektai ↓";
  else title.innerHTML = "Kai kurie mokykliniai projektai ↑";
  $("#m_proj").slideToggle();
});

